class Queue:
    def __init__(self):
        self._stack1 = []
        self._stack2 = []

    def enqueue(self, value):
        # Push into first stack until it becomes full
        while len(self._stack1) < capacity:
            self._stack1.append(value)

        # If one of them became full then pop from that until it has space available again
        if len(self._stack1) == capacity:
            while len(self._stack2):
                item = self._stack2.pop()

                self._stack1.append(item)

    def dequeue(self):
        return_val = self._stack1[0]
        del self._stack1[0]

        if not self._stack1 or len(self._stack1) > capacity*3/4:
            return None

        return return_val

capacity = int(input("Enter your desired maximum size for this queue: "))
myQueue = Queue()

print(f"Enqueing {capacity} items...")
for _ in range(capacity):
    myQueue.enqueue(input())

while True:
    try:
        print(myQueue.dequeue())
    except IndexError:
        break

print("\nDone.")
