int main() {
    // Open input file
    std::ifstream infile("input.txt");
    if (infile.is_open()) {
        // Read each line from the file
        std::string line;
        int count = 0;
        while (std::getline(infile, line)) {
            // Split the line into words
            std::istringstream iss(line);
            std::string word;
            while (iss >> word) {
                // Check if the current word is the one we want to count
                if (word == "example") {
                    // Increment the count
                    count++;
                }
            }
        }

        // Print out the result
        std::cout << "The word \"example\" appears " 
                  << count << " times in the file." 
                  << std::endl;

    } else {
        std::cerr << "Error opening input file." 
                  << std::endl;
    }

    return 0;
}
```

