int main() {  
    ifstream inputFile("text_file.txt");
    string line, word;
    int count = 0;

    while (getline(inputFile, line)) {
        // Store current line in variable
        if (count == 0) {
            word = line;            
        }

        if (line.find(word)!= string::npos){
            count++;
        }
    }
    return 0;
}

def lcs(str1, str2): 

    n = len(str1) 
    m = len(str2) 
    L[0] = [float('-inf') for i in range(m+1)] 
    R[0] = [float('-inf') for i in range(n+1)] 
    dp = [[0 for k in range(m+1)] for j in range(n+1)] 

    for i in range (1, n + 1):  
        for j in range (1, m + 1): 
            if (str1[i - 1] == str2[j - 1]): 
                dp[i][j] = 1 + max(dp[i - 1][j],    
                               dp[i][j - 1])     
            else: 
                dp[i][j] = max(dp[i - 1][j],      
                              dp[i][j - 1])    

    print("Longest Common Subsequence is : " + str([dp[-1]]) ) 

