def lcs(seq1, seq2): 
    m = len(seq1) + 1
    n = len(seq2) + 1

    # Initialize an empty L[m][n] array for storing lengths of all possible sub-sequences 
    L = [[0] * (n+1) for _ in range(m+1)] 

    # Fill out the first row and column of L[] 
    for i in range(1, m+1): 
        L[i][0] = 0
        for j in range(1, n+1): 
            if seq1[-j].isspace(): 
                L[i][j] = max(L[i-1][j],L[i][j-1]) + 1
            else: 
                L[i][j] = max(L[i-1][j]+1,L[i][j-1]+1,L[i-1][j-1]) 

    // Create a result matrix with dimensions m x n and fill it with 0s 
    R = [[0] * (n+1) for _ in range(m+1)] 

    // Run a loop over each element from 1 to n and print it on reaching its index in sequence 
    for k in range(1, n+1): 
        if(k > 0 and seq2[k - 1]!=''): 
             R[0][k] = L[0][k-1] 
        else : 
             R[0][k] = L[0][k] 

    // Run a loop over each row from 1 to m and print each non zero elements at respective columns 
    for i in range(1, m+1): 
        for j in range(1, n+1): 
            if(R[i][j]!= 0): 
                 print("lcs({}, {}) == {}".format(seq1, seq2,R[i][j])) 

    return R[0][n]

def fib(n):
    if (n == 0 or n == 1):
        return n
    else:
        return fib(n-2) + fib(n-1)

def binary_search(arr, x):
    low = 0
    high = len(arr) - 1

    while low <= high:
        mid = (low + high) // 2

        if arr[mid] < x:
            low = mid + 1

        elif arr[mid] > x:
            high = mid - 1

        else:
            return mid

    return -1 # Element not found

